-- 导出  表 wlmz.game_message 结构
CREATE TABLE IF NOT EXISTS `game_message` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '0,公共消息,1,私人消息,2，战报,3,系统消息,',
  `subType` tinyint(4) NOT NULL DEFAULT '5' COMMENT '0-0,聊天，0-1，公告，0-2帮助,0-3,其它系统消息，2-1，夺宝战报，2-2，征讨战报，3-1,好友请求,3-2,请求被通过，3-3，请求被拒绝,3-4，解除好友关系',
