  `sendTime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发送时间',
  `content` varchar(500) DEFAULT NULL COMMENT '内容',
  `refValue` varchar(5000) DEFAULT NULL COMMENT '2,3',
  `serverId` smallint(6) DEFAULT NULL COMMENT '服务器ID',
  `new` tinyint(4) NOT NULL DEFAULT '1' COMMENT '是否是新消息，首页显示个数用',
  `state` tinyint(4) NOT NULL DEFAULT '0' COMMENT '1,state=0表示未读,state=1表示已读,state=2表示删除,0，0-2,0-3，0-4,state=0表示状态不可显示在世界聊天中，stat=1表示可以显示在公共聊天中，2-1,state=0,未回应,1,回应成功，2，回应失败,3,回应忍了',
  PRIMARY KEY (`id`),
  KEY `game_messages_Receiver_Type_State_SendTime` (`receiver`,`type`,`state`,`sendTime`),
  KEY `game_messages_Sender_Type_State_SendTime` (`sender`,`type`,`state`,`sendTime`),
  KEY `game_messages_Sender_Receiver_Type_State_SendTime` (`sender`,`receiver`,`type`,`state`,`sendTime`),
  KEY `game_messages_SubType` (`subType`),
  KEY `game_messages_SendTime` (`sendTime`),
  KEY `game_messages_State` (`state`),
  KEY `game_messages_Type_SubType_State_SendTime` (`type`,`subType`,`state`,`sendTime`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

